    @Override
    public void onCreate(SQLiteDatabase db) {
        String query = "CREATE TABLE " + TABLE_NAME +
                " ("+COLUMN_ID + " INTEGER PRIMARY KEY AUTOINCREMENT,"+
                COLUMN_NAME + " TEXT, " +
                COLUMN_TYPE1 + " INT, " +
                COLUMN_TYPE2 + " INT, " +
                COLUMN_HEIGHT + " REAL, " +
                COLUMN_WEIGHT + " REAL, " +
                COLUMN_STAGE + " INT, " +
                COLUMN_REGION + " INT);";
        db.execSQL(query);

    }

    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        db.execSQL("DROP TABLE IF EXISTS " + TABLE_NAME);
        onCreate(db);
    }

    public void cargarBichos(){
        System.out.println(pokedex.size());
        SQLiteDatabase database = this.getWritableDatabase();
        for (int i =0; i < pokedex.size() ; i++){
            Pokemon data = pokedex.get(i);
            ContentValues cv = new ContentValues();
            cv.put(COLUMN_NAME,data.getNombre());
            cv.put(COLUMN_TYPE1,data.getTipo1());
            cv.put(COLUMN_TYPE2,data.getTipo2());
            cv.put(COLUMN_HEIGHT,data.getAltura());
            cv.put(COLUMN_WEIGHT,data.getPeso());
            cv.put(COLUMN_STAGE,data.getEtapaEvolutiva());
            cv.put(COLUMN_REGION,data.getRegion());
            long result = database.insert(TABLE_NAME,null,cv);
            System.out.printf("Pokemon %d Nombre %s Código %d\n",data.getNumero(),data.getNombre(),result);
        }
        System.out.println("fin if");
    }

    public boolean databaseCargada(){
        String query = "SELECT * FROM " +TABLE_NAME;
        SQLiteDatabase db = this.getReadableDatabase();
        if (db!= null){
            Cursor cursor = db.rawQuery(query,null);
            System.out.println(cursor.getCount());
            return cursor.getCount() > 0;
        }
        return false;
    }

